    /**
     * @dataProvider imageUrlProvider
     */
    public function testGetImageHeaders($url)
    {
        $headers = Media::getImageHeaders($url);
        $this->assertArrayHasKey('content_type', $headers);
        $this->assertArrayHasKey('download_content_length', $headers);
    }


    /**
     * @dataProvider mediaPagesProvider
     *
     * @param $check_field
     * @param $url
     */
    public function testGetMediaFromHtml($check_field, $url)
    {
        $media = Media::getMediaFromHtml($url);
        $this->assertNotEmpty($media[$check_field]);
    }

